def characterize_distortion_b(filepath):
    """Characterize distortion gaps in an audio file."""
    audio = AudioSegment.from_file(filepath, format="wav")
    if audio.channels > 1:
        audio = audio.set_channels(1)

    bit_depth = audio.sample_width * 8
    array_type = get_array_type(bit_depth)
    numeric_samples = array(array_type, audio.get_array_of_samples())
    samples = np.array(numeric_samples)

    silence_threshold = 500
    silence = np.where(np.abs(samples) <= silence_threshold)[0]
    contiguous_regions = np.split(silence, np.where(np.diff(silence) != 1)[0]+1)
    silence_gap_min_in_samples = int(audio.frame_rate * 100 / 1000)  # 100 ms in samples
    silence_gap_max_in_samples = int(audio.frame_rate * 250 / 1000)  # 250 ms in samples
    filtered_silence_periods = [s for s in contiguous_regions if silence_gap_min_in_samples <= len(s) <= silence_gap_max_in_samples]

    distortions = [(s[0], s[-1]) for s in filtered_silence_periods]
    return distortions


def extract_context_with_silent_gap(audio, gap_start, gap_end, context_length):
    """Extract context around a gap including a SILENT version of the gap."""
    before_gap = audio[gap_start-context_length:gap_start]
    silent_gap = np.zeros(gap_end - gap_start)  # Replace gap with silence
    after_gap = audio[gap_end:gap_end+context_length]

    return np.concatenate((before_gap, silent_gap, after_gap))


def extract_truth_context(audio, gap_start, gap_end, context_length):
    """Extract context around a gap including the ORIGINAL content of the gap."""
    before_gap = audio[gap_start-context_length:gap_start]
    within_gap = audio[gap_start:gap_end]  # Original content of the gap
    after_gap = audio[gap_end:gap_end+context_length]

    return np.concatenate((before_gap, within_gap, after_gap))


def process_audio_files(train_path, test_path):
    """Process audio files for training and testing."""
    # Load audio files preserving their original sampling rate
    train_audio, fs_train = librosa.load(train_path, sr=None)
    test_audio, fs_test = librosa.load(test_path, sr=None)

    # Detect gaps in the training audio
    gaps_train = characterize_distortion_b(train_path)

    # Define context length and target gap size
    context_length = int(0.2 * fs_train)  # 20% of the frame rate as context
    target_gap_size_samples = 9599

    # Filter gaps to match the target gap size
    exact_gaps_train = [gap for gap in gaps_train if (gap[1] - gap[0]) == target_gap_size_samples]

    # Extract audio segments with silent gaps for training
    X_train = [extract_context_with_silent_gap(train_audio, gap[0], gap[1], context_length) for gap in exact_gaps_train]

    # Extract corresponding segments with original gaps for target
    y_train = [extract_truth_context(test_audio, gap[0], gap[1], context_length) for gap in exact_gaps_train]

    return np.array(X_train), np.array(y_train)

  
def audio_to_tf_parts(segment, n_fft=M, hop_length=hop_length):
    """Convert an audio segment to its STFT real and imaginary parts."""
    stft = librosa.stft(segment[:, 0, 0], n_fft=n_fft, hop_length=hop_length, center=False)
    return np.real(stft), np.imag(stft)


def process_batch(segments):
    """Process a batch of audio segments into STFT representation."""
    real_part, imag_part = zip(*[audio_to_tf_parts(segment) for segment in segments])
    return np.stack((np.array(real_part), np.array(imag_part)), axis=-1)

def process_single_sample(sample):
    """Process a single audio sample into STFT representation."""
    parts = [sample[i:i+9599] for i in [0, 9599, 19198]]
    return np.concatenate([process_to_tf_representation(np.array([part])) for part in parts], axis=2)


def process_to_tf_representation(data, batch_size=100):
    """Process data into STFT representation in batches."""
    tf_list = [process_batch(data[i:i+batch_size]) for i in range(0, len(data), batch_size)]
    return np.concatenate(tf_list, axis=0)


def plot_spectrum(real_data, imag_data, hop_length=662, fs=48000):
    """Plot the magnitude spectrum of STFT data."""
    magnitude = np.sqrt(real_data**2 + imag_data**2)
    librosa.display.specshow(librosa.amplitude_to_db(magnitude), sr=fs, hop_length=hop_length, x_axis='frames', y_axis='log')
    plt.colorbar(label='Magnitude (dB)'); plt.xlabel('Frame number')
    plt.title('Spectrum for Single Gap and its Context')
    plt.axvline(x=27, color='r', linestyle='--', label="Gap Start")
    plt.axvline(x=56, color='r', linestyle='--', label="Gap End")
    plt.legend(); plt.show()

